import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDownWideShort, faClose, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import './Filter.css';
import { useRef } from 'react';
const Filter = () => {

    const filterRef = useRef();

    const handleFilterShow = () => {
        filterRef.current.classList.toggle('active')
    }

    const handleFilterClose = e => {
        filterRef.current.classList.toggle('active')
    }

    return(
        <div className="filter">
            <div className="container">
                <div className="filter-links">
                    <h2 className='active'>Nearest rides</h2>    
                    <h2>Upcoming rides (4)</h2>    
                    <h2>Past rides (2)</h2>    
                </div>
                <div onClick={handleFilterShow} className="filter-selects"><FontAwesomeIcon icon={faArrowDownWideShort} />
                    <h2>Filter</h2>
                    <div ref={filterRef} className='filter-lists'>
                        <div onClick={handleFilterClose} className='filter-close'><FontAwesomeIcon icon={faClose} /> Close</div>
                        <div className='filter-state filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'>State</div>
                        </div>
                        <div className='filter-city filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'>City</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Filter;
