import { useSelector } from "react-redux";
import { rideAdd } from "../../redux/reducers/RideSlice";

const NearestRides = dispatch => {
    
    const { ridesState,userState } = useSelector( state => state.rideStore );

    let userStationCode = userState.station_code;
    let ridesNearest = [];
    let rides_1 = ridesState.filter( el => {
        return el.station_path.includes( userStationCode )
    } );
    
    if(rides_1 !== undefined) {
        ridesNearest[0] = rides_1[0]
        if( !(ridesNearest.includes(undefined)) ){
            dispatch( rideUpload( { rides_data:ridesNearest } ) )
        }
    };
}
export default NearestRides;
