import { createSlice } from "@reduxjs/toolkit";

const testSlice = createSlice({
    name:'testSlice',
    initialState:{
        test:'from test slice'
    },
    reducers:{
        test: state => {
            state.test = 'the state is changed'
        }
    }
})

export const { test } = testSlice.actions;
export default testSlice.reducer;
