import { createSlice } from "@reduxjs/toolkit";

const RideSlice = createSlice({
    name:'RideSlice',
    initialState:{
        ridesState:[1]
    },
    reducers:{
        rideAdd: ( state, action ) => {
            console.log(typeof state.ridesState)
            let ridesData = action.payload.rides_data;
            console.log(typeof ridesData)
            Array.of(state.ridesState);
            state.ridesState = ridesData;
            //ridesData.map( (el, indx) => Array.of(state.ridesState[indx] = el) )
        }
    }
})

export const { rideAdd } = RideSlice.actions;
export default RideSlice.reducer;
