import { createSlice } from "@reduxjs/toolkit";

const RideSlice = createSlice({
    name:'RideSlice',
    initialState:{
        ridesDataState:[],
        data:0
    },
    reducers:{
        ridesAdd: ( state, action ) => {
            let ridesData = action.payload.rides_data;
            let dataActive = action.payload.data;
            state.ridesDataState = ridesData;
            state.data = dataActive;
        },
    }
})

export const { ridesAdd } = RideSlice.actions;
export default RideSlice.reducer;
