import { createSlice } from "@reduxjs/toolkit";

const OriginalRidesSlice = createSlice({
    name:'OriginalRidesSlice',
    initialState:{
        originalRidesState:[],
        stateSelect:'all',
        citySelect:'all'
    },
    reducers:{
        originalRidesAdd: ( state, action ) => {
            let ridesData = action.payload.originalRides;
            state.originalRidesState = ridesData;
            console.log('from original')
        },
        originalRidesStateOrCity: (state, action) => {
            if( action.payload.stateSelect ){
                state.stateSelect = action.payload.stateSelect;
            }
            if( action.payload.citySelect ){
                state.citySelect = action.payload.citySelect;
            }
        }
    }
})

export const { originalRidesAdd, originalRidesStateOrCity } = OriginalRidesSlice.actions;
export default OriginalRidesSlice.reducer;
