import { configureStore } from "@reduxjs/toolkit";
import ridesData from "./reducers/ridesData";
import RideSlice from "./reducers/RideSlice";

const Store = configureStore({
    reducer:{
        rideStore:RideSlice,
        rideDataStore:ridesData
    }
});

export default Store;
