const DateFunc = dateValue => {

    let getDateFormat = dateValue;

    let months = [ 'Jan' , 'Feb' , 'Mar' , 'Apr' , 'May' , 'June' , 'July' , 'Aug' , 'Sep' , 'Oct' , 'Nov' , 'Dec' ];
    let month = months[getDateFormat.getMonth()];

    let year = getDateFormat.getFullYear(); 
    let day = getDateFormat.getDay(); 
    let hours = getDateFormat.getHours(); 
    let minutes = getDateFormat.getMinutes(); 

    return { day, month, year, hours, minutes }
    
}
export default DateFunc;
