import axios from 'axios';
import { rideAdd } from '../redux/reducers/RideSlice';

const ApiRides = async (userState) => {

    let rides = [];

    const setAllRides = data => {
        return rides = data
    }

    await axios.get('https://assessment.api.vweb.app/rides').then(

        res => {return setAllRides(res.data)}

    )

    rides.map( el => {
        console.log( el.station_code )
    })

} 

export default ApiRides;
