import axios from 'axios';
import { useState } from 'react';
import { rideAdd } from '../redux/reducers/RideSlice';

const ApiRides = dispatch => {

    const [ allRides, setAllRides ] = useState();

    axios.get('https://assessment.api.vweb.app/rides').then(

        res => {return setAllRides(res.data)}

    )

    let rides = allRides;
    let data = 0;

    if( data !== 1 ){
        dispatch( rideAdd( { rides_data:rides } ) )
        return data = 1;
    }


} 

export default ApiRides;
