import { createSlice } from "@reduxjs/toolkit";

const RideSlice = createSlice({
    name:'RideSlice',
    initialState:{
        ridesState:'from ride slice'
    },
    reducers:{
        rideAdd: state => {
            state.ridesState = 'the state is changed'
        }
    }
})

export const { rideAdd } = RideSlice.actions;
export default RideSlice.reducer;
