import { createSlice } from "@reduxjs/toolkit";

const RideSlice = createSlice({
    name:'RideSlice',
    initialState:{
        ridesState:[],
        userStation:0
    },
    reducers:{
        rideAdd: ( state, action ) => {
            let ridesData = action.payload.rides_data;
            state.ridesState = ridesData;
        },
        addUserStation: ( state, action ) => {
            state.userStation = action.payload.userStationValue;
        }

    }
})

export const { rideAdd, addUserStation } = RideSlice.actions;
export default RideSlice.reducer;
