import axios from 'axios';
import { rideAdd } from '../redux/reducers/RideSlice';

const ApiRides = async (setRides,userStateCode) => {
    await axios
            .get('https://assessment.api.vweb.app/rides')
            .then( res => setRides(rideAdd({rides_data:res.data, userStateCode:userStateCode})) )
} 

export default ApiRides;
