import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDownWideShort, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import './Filter.css';
import { useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import FilterUpcoming from './FilterFuncs/FilterUpcoming';
import { rideAdd } from '../../redux/reducers/RideSlice';
const Filter = () => {
    const dispatch = useDispatch();
    const { originalRidesState, stateSelect, citySelect } = useSelector( state => state.originalStore );
    const { ridesState, upcomingCount, pastCount } = useSelector( state => state.rideStore );
    const filterRef = useRef();
    const stateRef = useRef();
    const cityRef = useRef();

    const handleFilterShow = e => {
        e.target.parentElement.classList.toggle('active')
        filterRef.current.classList.toggle('active')
    }

    const handleFilterSelect = refValue => {
        refValue.current.classList.toggle('active')
    }

    const handleFilterUpcoming = e => {
        e.target.previousSibling.classList.remove('active')
        e.target.nextSibling.classList.remove('active')
        e.target.classList.add('active')
        FilterUpcoming(ridesState,dispatch,rideAdd);
    }

    return(
        <div className="filter">
            <div className="container">
                <div className="filter-links">
                    <h2 className='active'>Nearest rides</h2>    
                    <h2 onClick={handleFilterUpcoming}>Upcoming rides ({upcomingCount})</h2>    
                    <h2>Past rides ({pastCount})</h2>    
                </div>
                <div className="filter-selects">
                    <div onClick={handleFilterShow} className='filter-header'>
                        <FontAwesomeIcon icon={faArrowDownWideShort} />
                        <h2>Filter</h2>
                    </div>
                    <div ref={filterRef} className='filter-lists'>
                        <div onClick={() => handleFilterSelect(stateRef)} className='filter-state filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'><h2>State ({stateSelect})</h2></div>
                        </div>
                        <div ref={stateRef} className='filter-state-list'>
                            <div className='filter-single-list'>
                                <div className='filter-select-style'>all</div>
                            </div>
                                { 
                                originalRidesState.length > 0 &&
                                    originalRidesState.map( (el,indx) => {
                                        return(
                                            <div className='filter-single-list' key={indx}>
                                                <div  className='filter-select-style'>{el.state}</div>
                                            </div>
                                        )
                                    } ) }
                        </div>
                        <div onClick={() => handleFilterSelect(cityRef)} className='filter-city filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'><h2>City ({citySelect})</h2></div>
                        </div>
                        <div ref={cityRef} className='filter-state-list'>
                            <div className='filter-single-list'>
                                <div className='filter-select-style'>all</div>
                            </div>
                                { 
                                originalRidesState.length > 0 &&
                                    originalRidesState.map( (el,indx) => {
                                        return(
                                            <div className='filter-single-list' key={indx}>
                                                <div  className='filter-select-style'>{el.city}</div>
                                            </div>
                                        )
                                    } ) }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Filter;
