import axios from 'axios';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { rideAdd } from '../redux/reducers/RideSlice';
import './Rides.css';

const Rides = () => {

    const [ rides_data, setRides_data ] = useState();
    const { ridesState } = useSelector( state => state.rideStore )
    const dispatch = useDispatch();

    const apiTest = setRides => {
        axios.get('https://assessment.api.vweb.app/rides')
             .then( res => setRides(rideAdd({rides_data:res.data})) )
    } 

    useEffect(()=>{
        apiTest( dispatch )
    },[])

    return(
        <div className="rides">
            <div className="container">
                { rides_data !== undefined && 
                    rides_data.map( (el, indx) => {
                        if(indx < 4){
                            return(
                                <div className="ride-single" key={ el.id }>
                                    <div className='ride-single-content'>
                                        <div className='ride-single-img'></div>
                                        <div className='ride-single-data'>
                                            <h2>Ride Id : <span>{ el.id }</span></h2>
                                            <h2>Origin Station : <span>{ el.id }</span></h2>
                                            <h2>station_path : <span>[20, 39, 40, 42, 54, 63, 72, 88, 98]</span></h2>
                                            <h2>Date : <span>15th Feb 2022 16:33</span></h2>
                                            <h2>Distance : <span>0</span></h2>
                                        </div>
                                    </div>
                                    <div className='ride-single-content'></div>
                                </div>
                            )
                        }
                    }) }
            </div>
        </div>
    )
}
export default Rides;
