import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDownWideShort, faClose, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import './Filter.css';
import { useRef } from 'react';
const Filter = () => {

    const filterRef = useRef();

    const handleFilterShow = () => {
        filterRef.current.classList.add('active')
    }

    return(
        <div className="filter">
            <div className="container">
                <div className="filter-links">
                    <h2 className='active'>Nearest rides</h2>    
                    <h2>Upcoming rides (4)</h2>    
                    <h2>Past rides (2)</h2>    
                </div>
                <div onClick={handleFilterShow} className="filter-selects"><FontAwesomeIcon icon={faArrowDownWideShort} />
                    <h2>Filter</h2>
                    <div ref={filterRef} className='filter-lists'>
                        <div><FontAwesomeIcon icon={faClose} /> Close</div>
                        <div className='filter-state filter-single-list'><FontAwesomeIcon icon={faArrowDown} /> State</div>
                        <div className='filter-city filter-single-list'><FontAwesomeIcon icon={faArrowDown} /> City</div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Filter;
