import axios from 'axios';
import { useState } from 'react';

const ApiRides = () => {

    const [ allRides, setAllRides ] = useState();

    axios.get('https://assessment.api.vweb.app/rides').then(
        res => setAllRides(res.data) 
    )
    return allRides
} 

export default ApiRides;
