import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { addUserStation } from "../../redux/reducers/RideSlice";

const NearestRides = () => {
    const { userState } = useSelector( state => state.userStore );
    const dispatch = useDispatch();
    const addUserStationState = () => {
        dispatch( addUserStation( { userStationValue:userState.station_code } ) )
    }
    return addUserStationState() !== undefined && addUserStationState();
}
export default NearestRides;
