import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDownWideShort, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import './Filter.css';
import { useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import { originalRidesAddNearest, originalRidesAddUpcoming, originalRidesAddPast } from '../../redux/reducers/OriginalRidesSlice';

const Filter = () => {
    const dispatch = useDispatch();
    const [stateSelect, setStateSelect] = useState('all');
    const [citySelect, setCitySelect] = useState('all');
    const { ridesNearState, ridesUpcomingState, ridesPastState, upcomingCount, pastCount } = useSelector( state => state.rideStore );
    const filterRef = useRef();
    const stateRef = useRef();
    const cityRef = useRef();

    const handleFilterShow = e => {
        e.target.parentElement.classList.toggle('active')
        filterRef.current.classList.toggle('active')
    }

    const handleFilterSelect = refValue => {
        refValue.current.classList.toggle('active')
    }

    const handleFilterSelectValue = e => {
        let filterSelectValue = e.target.getAttribute('name');
        let filterField = e.target.parentElement.parentElement.id;
        /*if( filterField === 'state' ){
            setStateSelect(filterSelectValue)
        }else if(filterField === 'city'){
            setCitySelect(filterSelectValue)
        }*/
    }
    const handleFilterStateAndCity = () => {
        /*if( stateSelect !== 'all' && citySelect === 'all' ){
            let originalNear = ridesNearState.filter( el => el.state === stateSelect )
            dispatch( originalRidesAddNearest( { originalRidesNearest : originalNear } ) )
            let originalUpcoming = ridesUpcomingState.filter( el => el.state === stateSelect )
            dispatch( originalRidesAddUpcoming( { originalRidesUpcoming : originalUpcoming } ) )
            let originalPast = ridesPastState.filter( el => el.state === stateSelect )
            dispatch( originalRidesAddPast( { originalRidesPast : originalPast } ) )
        }else if( stateSelect === 'all' && citySelect === 'all' ){
            let originalNear = ridesNearState;
            dispatch( originalRidesAddNearest( { originalRidesNearest : originalNear } ) )
            let originalUpcoming = ridesUpcomingState;
            dispatch( originalRidesAddUpcoming( { originalRidesUpcoming : originalUpcoming } ) )
            let originalPast = ridesPastState;
            dispatch( originalRidesAddPast( { originalRidesPast : originalPast } ) )
        }    */
        console.log('from handle')
    }

    handleFilterStateAndCity();
    
    return(
        <div className="filter">
            <div className="container">
                <div className="filter-links">
                <NavLink className={ ({isActive}) => isActive ? 'active' : '' } to={`/`}>
                    <h2>Nearest rides</h2>    
                </NavLink> 
                <NavLink className={ ({isActive}) => isActive ? 'active' : '' } to={`upcoming`}>
                    <h2>Upcoming rides ({upcomingCount})</h2>    
                </NavLink>
                <NavLink className={ ({isActive}) => isActive ? 'active' : '' } to={`past`}>
                    <h2>Past rides ({pastCount})</h2>    
                </NavLink>       
                </div>
                <div className="filter-selects">
                    <div onClick={handleFilterShow} className='filter-header'>
                        <FontAwesomeIcon icon={faArrowDownWideShort} />
                        <h2>Filter</h2>
                    </div>
                    <div ref={filterRef} className='filter-lists'>
                        <div onClick={() => handleFilterSelect(stateRef)} className='filter-state filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'><h2>State ({stateSelect})</h2></div>
                        </div>
                        <div ref={stateRef} id='state' className='filter-state-list'>
                            <div onClick={handleFilterSelectValue} className='filter-single-list'>
                                <div className='filter-select-style' name='all'>all</div>
                            </div>
                                { 
                                ridesNearState.length > 0 &&
                                    ridesNearState.map( (el,indx) => {
                                        return(
                                            <div onClick={handleFilterSelectValue} className='filter-single-list' key={indx}>
                                                <div className='filter-select-style' name={el.state}>{el.state}</div>
                                            </div>
                                        )
                                    } ) }
                        </div>
                        <div onClick={() => handleFilterSelect(cityRef)} className='filter-city filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'><h2>City ({citySelect})</h2></div>
                        </div>
                        <div ref={cityRef} id='city' className='filter-state-list'>
                            <div onClick={handleFilterSelectValue} className='filter-single-list'>
                                <div className='filter-select-style' name='all'>all</div>
                            </div>
                                { 
                                ridesNearState.length > 0 &&
                                    ridesNearState.map( (el,indx) => {
                                        return(
                                            <div onClick={handleFilterSelectValue} className='filter-single-list' key={indx}>
                                                <div  className='filter-select-style' name={el.city}>{el.city}</div>
                                            </div>
                                        )
                                    } ) }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Filter;
