import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDownWideShort, faArrowDown } from '@fortawesome/free-solid-svg-icons';
import './Filter.css';
import { useRef } from 'react';
import { useSelector } from 'react-redux';

const Filter = () => {

    const { originalRidesState } = useSelector( state => state.originalStore );
    const filterRef = useRef();

    const handleFilterShow = () => {
        filterRef.current.classList.toggle('active')
    }

    return(
        <div className="filter">
            <div className="container">
                <div className="filter-links">
                    <h2 className='active'>Nearest rides</h2>    
                    <h2>Upcoming rides (4)</h2>    
                    <h2>Past rides (2)</h2>    
                </div>
                <div onClick={handleFilterShow} className="filter-selects"><FontAwesomeIcon icon={faArrowDownWideShort} />
                    <h2>Filter</h2>
                    <div ref={filterRef} className='filter-lists'>
                        <div className='filter-state filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'><h2>State</h2></div>
                        </div>
                        <div className='filter-state-list'>
                            <div className='filter-state filter-single-list'>
                                <div className='filter-select-style'>all</div>
                            </div>
                                { 
                                originalRidesState.length > 0 &&
                                    originalRidesState.map( (el,indx) => {
                                        return(
                                            <div className='filter-state filter-single-list' key={indx}>
                                                <div  className='filter-select-style'>{el.state}</div>
                                            </div>
                                        )
                                    } ) }
                        </div>
                        <div className='filter-city filter-single-list'>
                            <div className='filter-select-style'><FontAwesomeIcon icon={faArrowDown} /></div> 
                            <div className='filter-select-style'><h2>City</h2></div>
                        </div>
                        <div className='filter-state-list'>
                            <div className='filter-state filter-single-list'>
                                <div className='filter-select-style'>all</div>
                            </div>
                                { 
                                originalRidesState.length > 0 &&
                                    originalRidesState.map( (el,indx) => {
                                        return(
                                            <div className='filter-state filter-single-list' key={indx}>
                                                <div  className='filter-select-style'>{el.city}</div>
                                            </div>
                                        )
                                    } ) }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default Filter;
